const postVariants = {initial:{ opacity: 0 }, enter:{ opacity: 1 }, exit:{ opacity: 0 }};

class Home extends Component {
  componentDidMount() {
    var section = "";
    var title = "Yasmro's Portfolio 2020"
    document.title = section + title;
  }

  render(){
    return(
      <div className="bg-dark vh-100 text-center" Style="margin-top:-56px;">
        <div className="position-absolute pl-lg-5 container-fluid text-white overflow-hidden bg-dark " Style = "top: calc(10vh);" id="topmessage">
        <AnimatePresence exitBeforeEnter>
          <motion.div initial="initial" animate="enter" exit="exit" key="home">
            <div className = "mb-3">
              <motion.div variants={postVariants}>
                <img src={require('../images/topName.svg')} className="aligncenter mr-2 mb-5" id="toplogo" alt=""/>
                <h1 className="h1">Move Emotion</h1>
              </motion.div>
              <motion.div variants={postVariants}>
                <h2 className="h5">Yu Ohno's Portfolio</h2>
              </motion.div>
            </div>
            <motion.div variants={postVariants} className="mb-4 text-center">
              <span className="w-50">Hi, I'm Yu Ohno from Japan and post-support engineer (mainly), calligrapher, artist and designer. </span>
            </motion.div>

            <motion.ul variants={postVariants} className="list-unstyled pl-0 d-inline-block text-center " id="topmenu">
              <div className="pt-2 pb-2"><Link className="h5 text-white mb-3" to={process.env.PUBLIC_URL + "/about"}>About</Link></div>
              <div className="pt-2 pb-2"><Link className="h5 text-white mb-3" to={process.env.PUBLIC_URL + "/works"}>Works</Link></div>
              <div className="pt-2 pb-2"><Link className="h5 text-white mb-3" to={process.env.PUBLIC_URL + "/contact"}>Contact</Link></div>
            </motion.ul>

          </motion.div>

          {/* <motion.div initial="initial" animate="enter" exit="exit" key="home" className="position-absolute" style = {{"top": 70 + "vh"}} id="topmenu">
            <motion.ul variants={postVariants} className="text-decoration-none list-unstyled pl-0">
              <li className="h4"><Link className="text-white text-decoration-none" to={process.env.PUBLIC_URL + "/about"}>About</Link></li>
              <li className="h4"><Link className="text-white text-decoration-none" to={process.env.PUBLIC_URL + "/works"}>Works</Link></li>
              <li className="nav-item"><Link className="nav-link" to={process.env.PUBLIC_URL + "/interests"}>Interests</Link></li>
              <li className="h4"><Link className="text-white text-decoration-none" to={process.env.PUBLIC_URL + "/contact"}>Contact</Link></li>
            </motion.ul>

          </motion.div> */}
          </AnimatePresence>
        </div>
      </div>
    )
  }
}
export default Home;
