const testSketch = (p) => {
    let rotation = 0
    let xspacing = 1; // Distance between each horizontal location
    let w; // Width of entire wave
    let theta = 0.0; // Start angle at 0
    let amplitude = 8; // Height of wave
    let period = 500.0; // How many pixels before the wave repeats
    let dx; // Value for incrementing x
    let yvalues; // Using an array to store height values for the wave
    let TWO_PI = 2*3.14159;

    //初期設定
    p.setup = () => {
        // p.createCanvas(p.windowWidth, p.windowHeight, p.WEBGL)
        p.createCanvas(p.windowWidth,200)
        w = p.windowWidth + 1;
        dx = (TWO_PI / period) * xspacing;
        yvalues = new Array(Math.floor(w / xspacing));
    }

    //P5Wrapperコンポーネントからのpropsの受け渡し
    p.myCustomRedrawAccordingToNewPropsHandler = (props) => {
        if (props.rotation){
            rotation = props.rotation * Math.PI / 180
        }
    }

    //毎フレームごとの描画内容
    p.draw = () => {
        p.background(255)
        p.calcWave();
        p.renderWave();
        // p.normalMaterial()
        // p.noStroke()
        // p.push()
        // p.rotateY(rotation)
        // p.box(100)
        // p.pop()
    }

    p.calcWave = () => {
        // Increment theta (try different values for
        // 'angular velocity' here)
        theta += 0.02;

        // For every x value, calculate a y value with sine function
        let x = theta;
        for (let i = 0; i < yvalues.length; i++) {
          yvalues[i] = Math.sin(x) * amplitude;
          x += dx;
        }
    }

    p.renderWave = () => {
        p.noStroke();
        p.fill(40);
        // A simple way to draw the wave with an ellipse at each location
        for (let x = 0; x < yvalues.length; x++) {
          p.ellipse(x * xspacing, p.windowHeight / 2 + yvalues[x], 1, 1);
        }
    }




}

export default testSketch
