import React, { Component } from 'react';
import { motion, AnimatePresence } from "framer-motion";
import {BrowserRouter, Switch, Route, Link, useLocation } from 'react-router-dom';

let easing = [0.175, 0.85, 0.42, 0.96];

// const postVariants = {
//   initial: { scale: 1, y: 100, opacity: 0},
//   enter: { scale: 1, y: 0, opacity: 1, transition: { duration: 0.5, ease: [0.48, 0.15, 0.25, 0.96] } },
//   exit: {
//     scale: 0.6,
//     y: 0,
//     opacity: 0,
//     transition: { duration: 0.2, ease: [0.48, 0.15, 0.25, 0.96] }
//   }
// };

const postVariants = {initial:{ opacity: 0 }, enter:{ opacity: 1 }, exit:{ opacity: 0 }};

class Home extends Component {
  componentDidMount() {
    var section = "";
    var title = "Yasmro's Portfolio 2020"
    document.title = section + title;
  }

  render(){
    return(
      <div className="bg-dark vh-100" Style="margin-top:-56px;">
        <div className="position-absolute pl-lg-5 container text-white overflow-hidden bg-dark" Style = "top: calc(10vh);" id="topmessage">
        <AnimatePresence exitBeforeEnter>
          <motion.div initial="initial" animate="enter" exit="exit" key="home">
            <div className = "mb-3">
              <motion.div variants={postVariants}>
                <img src={require('../images/name_black.png')} className="aligncenter mr-2" alt=""/>
                <h1 className="display-3 d-none d-md-block">Move Emotion</h1>
                <h1 className="display-4 d-block d-md-none">Move Emotion</h1>
              </motion.div>
              <motion.div variants={postVariants}>
                <h2 className="h2 d-none d-md-block">Yu Ohno's Portfolio</h2>
                <h2 className="h3 d-block d-md-none">Yu Ohno's Portfolio</h2>
              </motion.div>
            </div>
            <motion.div variants={postVariants} className="mb-4 w-75">
              <p>Hi, I'm Yu Ohno from Japan and post-support engineer (mainly), calligrapher, artist and designer. </p>
            </motion.div>

            <motion.ul variants={postVariants} className="list-unstyled pl-0 d-block d-md-flex" id="topmenu">
              <li><Link className="h4 text-white pt-2 pr-5 " to={process.env.PUBLIC_URL + "/about"}>About</Link></li>
              <li><Link className="h4 text-white pt-2 pr-5 " to={process.env.PUBLIC_URL + "/works"}>Works</Link></li>
              <li><Link className="h4 text-white pt-2 pr-5 " to={process.env.PUBLIC_URL + "/contact"}>Contact</Link></li>
            </motion.ul>

          </motion.div>

          {/* <motion.div initial="initial" animate="enter" exit="exit" key="home" className="position-absolute" style = {{"top": 70 + "vh"}} id="topmenu">
            <motion.ul variants={postVariants} className="text-decoration-none list-unstyled pl-0">
              <li className="h4"><Link className="text-white text-decoration-none" to={process.env.PUBLIC_URL + "/about"}>About</Link></li>
              <li className="h4"><Link className="text-white text-decoration-none" to={process.env.PUBLIC_URL + "/works"}>Works</Link></li>
              <li className="nav-item"><Link className="nav-link" to={process.env.PUBLIC_URL + "/interests"}>Interests</Link></li>
              <li className="h4"><Link className="text-white text-decoration-none" to={process.env.PUBLIC_URL + "/contact"}>Contact</Link></li>
            </motion.ul>

          </motion.div> */}
          </AnimatePresence>
        </div>
      </div>
    )
  }
}
export default Home;
