import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter, Route, Link} from 'react-router-dom';

import { state as portfolios } from '../data/portfolios';

import { motion } from "framer-motion";

const variants = {
  open: {
    transition: { staggerChildren: 0.07, delayChildren: 0.2 }
  },
  closed: {
    transition: { staggerChildren: 0.05, staggerDirection: -1 }
  }
};

const variantsli = {
  open: {
    y: 0,
    opacity: 1,
    transition: {
      y: { stiffness: 1000, velocity: -100 }
    }
  },
  closed: {
    y: 50,
    opacity: 0,
    transition: {
      y: { stiffness: 1000 }
    }
  }
};


class Works extends Component {
  constructor() {
    super();
    this.state = portfolios;
  }


  render(){
    const categories = this.state.works.categories.map( (category, i) =>

        <Link className="nav-link col-lg-5 mr-lg-2 col-12 mb-2 bg-light text-dark" key={i} to={process.env.PUBLIC_URL + "/works/" + category.id}>
          <div className="cover-img pt-2 pr-md-2 works-category-box" Style="background-image:url('../images/shodo/1.png');">
            <div className="about-text">
              <h3>{category.name}</h3>
              <p>{category.description}</p>
            </div>
          </div>
        </Link>

    )

    return(
      <div className="container mt-5">
        <motion.div
        animate={{
          y: 0,
          opacity: 1
        }}
        initial={{
          y: 100,
          opacity: 0
        }}
        exit={{
          y: -100,
          opacity: 0
        }}
        transition={{
          duration: 0.2
        }}
     >
        <h1 className="mb-5 text-center text-md-left">Works</h1>
      </motion.div>
      <motion.div
        animate={{
          y: 0,
          opacity: 1
        }}
        initial={{
          y: 100,
          opacity: 0
        }}
        exit={{
          y: -100,
          opacity: 0
        }}
        transition={{
          delay:0.2,
          duration: 0.2
        }}
     >
        <div className="container">
        <motion.div
            className="row"
            initial="initial"
            animate="enter"
            exit="exit"
            variants={{ initial: { transition: { staggerChildren: 0.1 } } }}
        >
            {categories}
        </motion.div>




        </div>
      </motion.div>
      </div>

    )
  }


}


export default Works;
