import React, { Component } from 'react';
import {BrowserRouter, Route, Link} from 'react-router-dom';
import { motion } from "framer-motion";

import { state as portfolios } from '../data/portfolios';

import '../style.css';

class WorkDetail extends React.Component {

  constructor() {
    super();
    this.state = portfolios;
  }

  render(){
    const category_id = this.props.match.params.category;
    const id = this.props.match.params.id - 1;
    const category = this.state.works[category_id];
    const work = category.portfolios[id];

    var description;
    try{
      description = this.state.works[category_id].portfolios[id].description.map( section =>
        <div>
          {
            section.type == 'paragraph' &&
            <div>
              <h4>{section.header}</h4>
              <p>{section.contents}</p>
            </div>
          }
          {
            section.type == 'link' &&
            <div>
              <h4>{section.header}</h4>
              { section.links.map((link) => <a href={link.href}>{link.name}</a>)}
            </div>
          }
        </div>

      )
    }catch{

    }

    return(

      <div className="container mt-5">
        <motion.div
      animate={{
        y: 0,
        opacity: 1
      }}
      initial={{
        y: 100,
        opacity: 0
      }}
      exit={{
        y: -100,
        opacity: 0
      }}
      transition={{
        duration: 0.2
      }}
   >
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb bg-white pl-0">
            <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL + "/works/"}>Works</Link></li>
            <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL + "/works/" + category_id + "/"}>{category.name}</Link></li>
            <li className="breadcrumb-item active" aria-current="page">{work.title}</li>
          </ol>
        </nav>
        <h4><span class="badge badge-secondary">{work.category}</span></h4>
        <h2>{work.title}</h2>
        </motion.div>
        <motion.div
      animate={{
        y: 0,
        opacity: 1
      }}
      initial={{
        y: 100,
        opacity: 0
      }}
      exit={{
        y: -100,
        opacity: 0
      }}
      transition={{
        duration: 0.2,
        delay:0.2
      }}
      >
        <p>{work.abstract}</p>
        </motion.div>

        <motion.div
      animate={{
        y: 0,
        opacity: 1
      }}
      initial={{
        y: 100,
        opacity: 0
      }}
      exit={{
        y: -100,
        opacity: 0
      }}
      transition={{
        duration: 0.2,
        delay:0.4
      }}
      >
        <div className="pt-1">
          {description}
        </div>
        </motion.div>
      </div>


      )


  }
}

export default WorkDetail;
