import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter, Route, Link} from 'react-router-dom';

class Worklist extends React.Component {
  constructor() {
    super();
    this.state = {
      works: {
        shodo: {
          id: "shodo",
          name: "Shodo",
          description: "Experienced since 2009 and took 20+ awards on Japanese calligraphy concours before.",
          imageUrl: "shodo",
          portfolios:[
             { id:1,
              title:"Shodo1",
              description: "hoge1",
              images:[
                "test1"
              ]
            },
            {
              id:2,
              title:"Shodo2",
              description: "hoge1",
              images:[
                "test2"
              ]
            }

            ]
        },
        watchface: {
          id: "watchface",
          name: "Watchface",
          description: "Design and Develop with XML, CSS and JavaScript on Fitbit Ionic",
          imageUrl: "watchface",
          portfolios:[
            { id:1,
             title:"Watchface1",
             description: "hoge1",
             images:[
               "test1"
             ]
           },
           {
             id:2,
             title:"Watchface2",
             description: "hoge1",
             images:[
               "test2"
             ]
           }

          ]
        },
        miscelleneous: {
          id: "miscelleneous",
          name: "Miscelleneous",
          description: "Others",
          imageUrl: "mis",
          portfolios:[
            { id:1,
             title:"Mis1",
             description: "sss",
             images:[
               "test1"
             ]
           },
           {
             id:2,
             title:"Mis2",
             description: "hoge1",
             images:[
               "test2"
             ]
           }

          ]
        }
      }
    };
  }

  render(){
    const category = this.props.match.params.category;
    var category_desc = this.props.match.params.description;
    if(category == "shodo"){
      category_desc = "Experienced since 2009 and took 20+ awards on Japanese calligraphy concours before."
    }else if(category == "watchface"){
      category_desc = "Design and Develop with XML, CSS and JavaScript on Fitbit Ionic"
    }

    const works = this.state.works[category].portfolios.map( work =>
      <Link className="nav-link col-lg-4 col-md-6 col-12" to={process.env.PUBLIC_URL + "/works/" + category + "/" + work.id}>
        <div className="cover-img pt-5 pb-5" Style="background-image:url('https://manablog.org/wp-content/uploads/2016/11/top-img.jpg');">
          <div className="about-text">
            <h3>{work.title}</h3>
          </div>
        </div>
      </Link>
    )

    return(
      <div className="container mt-5">
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb bg-white pl-0">
            <li className="breadcrumb-item"><Link to={process.env.PUBLIC_URL + "/works/"}>Works</Link></li>
            <li className="breadcrumb-item active" aria-current="page">{category}</li>
          </ol>
        </nav>
        <h2>{category}</h2>
        <p>{category_desc}</p>
        {works}

          <div className="row">
            <div className="contents-work col-lg-4 col-md-6 col-12">
              <div className="about-text">
                <div className="thumbnail">
                  <img src={require('../images/clock05.png')} alt="test" data-toggle="modal" data-target="#img_clock05" className = "bwWrapper"/>
                </div>
              </div>
              <div className="about-text">
                <div className="thumbnail">
                  <img src={require('../images/clock05.png')} alt="test" data-toggle="modal" data-target="#img_clock05" className = "bwWrapper"/>
                </div>
              </div>
            </div>
          </div>
      </div>
    )
  }
}
export default Worklist;
