import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter, Route, Link} from 'react-router-dom';

import { state as portfolios } from '../data/portfolios';

import { motion } from "framer-motion";

const container = {
  hidden: { opacity: 0 },
  show: {
    opacity: 1,
    transition: {
      delayChildren: 0.5,
      staggerDirection: -1
    }
  }
}

const item = {
  hidden: { opacity: 0 },
  show: { opacity: 1 }
}



class Works extends Component {

  componentDidMount() {
    var section = "Works";
    var title = " – Yasmro's Portfolio 2020"
    document.title = section + title;
  }

  constructor() {
    super();
    this.state = portfolios;
  }


  render(){
    const categories = this.state.works.categories.map( (category, i) =>
    <motion.div
    animate={{
      y: 0,
      opacity: 1
    }}
    initial={{
      y: 100,
      opacity: 0
    }}
    exit={{
      y: -100,
      opacity: 0
    }}
    transition={{
      duration: 0.2
    }}
    key = {i}
 >
        <Link className="nav-link col-lg-5 mr-lg-2 col-12 mb-2 bg-light text-dark" key={i} to={process.env.PUBLIC_URL + "/works/" + category.id}>
          <div className="cover-img pt-2 pr-md-2 works-category-box" Style="background-image:url('../images/shodo/1.png');">
            <div className="about-text">
              <h3>{category.name}</h3>
              <p>{category.description}</p>
            </div>
          </div>
        </Link>

      </motion.div>

    )

    return(
      <div className="container mt-5">
        <motion.div
    variants={container}
    initial="hidden"
    animate="show"
     >
        <h1 className="mb-5 text-center text-md-left">Works</h1>
      </motion.div>
      <motion.div
    variants={container}
    initial="hidden"
    animate="show"
     >
        <div className="container">
        <motion.div
            className="row"
            variants={container}
            initial="hidden"
            animate="show"
        >
            {categories}
        </motion.div>




        </div>
      </motion.div>
      </div>

    )
  }


}


export default Works;
