import React, { Component } from 'react';
import { motion, AnimatePresence } from "framer-motion";
import {BrowserRouter, Switch, Route, Link, useLocation } from 'react-router-dom';

let easing = [0.175, 0.85, 0.42, 0.96];

// const postVariants = {
//   initial: { scale: 1, y: 100, opacity: 0},
//   enter: { scale: 1, y: 0, opacity: 1, transition: { duration: 0.5, ease: [0.48, 0.15, 0.25, 0.96] } },
//   exit: {
//     scale: 0.6,
//     y: 0,
//     opacity: 0,
//     transition: { duration: 0.2, ease: [0.48, 0.15, 0.25, 0.96] }
//   }
// };

const postVariants = {initial:{ opacity: 0 }, enter:{ opacity: 1 }, exit:{ opacity: 0 }};
// const postVariants2 = {initial:{ opacity: 0 }, enter:{ opacity: 1, dela }, exit:{ opacity: 0 }, transition:{duration: 0.2, delay:20}};

class Home extends Component {
  componentDidMount() {
    var section = "";
    var title = "Yasmro's Portfolio 2020"
    document.title = section + title;
  }

  render(){
    return(
      <div className="bg-dark vh-100 vw-100" Style="margin-top:-56px;">
        <div className="pl-lg-5 container-fluid text-white bg-dark w-100 h-100" id="topmessage">
        <AnimatePresence exitBeforeEnter>
          <motion.div initial="initial" animate="enter" exit="exit" transition="transition" key="home">
            <div className = "">
              <motion.div variants={postVariants} className="position-absolute align-middle" style={{"top": 28 + "%", "left": 42 + "%"}}>
                <div id="my-div"></div>

                <img src={require('../images/topName.svg')} className=" mr-2 mb-5" style={{"min-height": "160px", "max-height": "260px"}} id="toplogo" alt=""/>

              </motion.div>

              <motion.div variants={postVariants} className=" position-absolute" style={{"bottom": 3 + "%", "left": 3 + "%"}}>
                <h1 className="h2">Move Emotion</h1>
                <h2 className="h6">Yu Ohno's Portfolio</h2>
                <div className="w-75 w-lg-50">
                  <span className="">Hi, I'm Yu Ohno from Japan and post-support engineer (mainly), calligrapher, artist and designer. </span>
                </div>

              </motion.div>
            </div>


            <motion.ul animate={{
          x: 0,
          opacity: 1
        }}
        initial={{
          x: 100,
          opacity: 0
        }}
        exit={{
          x: -100,
          opacity: 0
        }}
        transition={{
          duration: 1,
          delay:0.5
        }} className="list-unstyled pl-0 d-inline-block d-lg-none text-right position-absolute" style={{"top": 2 + "%", "right": 1 + "%"}} id="topmenu">
              <div className="pt-2 pb-2"><Link className="h4 text-white mb-3 px-4" to={process.env.PUBLIC_URL + "/about"}>About</Link></div>
              <div className="pt-2 pb-2"><Link className="h4 text-white mb-3 px-4" to={process.env.PUBLIC_URL + "/works"}>Works</Link></div>
              <div className="pt-2 pb-2"><Link className="h4 text-white mb-3 px-4" to={process.env.PUBLIC_URL + "/contact"}>Contact</Link></div>
            </motion.ul>

            <motion.ul animate={{
          x: 0,
          opacity: 1
        }}
        initial={{
          x: 100,
          opacity: 0
        }}
        exit={{
          x: -100,
          opacity: 0
        }}
        transition={{
          duration: 1,
          delay:0.5
        }} className="list-unstyled pl-0 d-none d-lg-inline-block text-right position-absolute " style={{"top": 2 + "%", "right": 1 + "%"}} id="topmenu">
              <div className="pt-2 pb-2"><Link className="h3 text-white mb-3 px-4" to={process.env.PUBLIC_URL + "/about"}>About</Link></div>
              <div className="pt-2 pb-2"><Link className="h3 text-white mb-3 px-4" to={process.env.PUBLIC_URL + "/works"}>Works</Link></div>
              <div className="pt-2 pb-2"><Link className="h3 text-white mb-3 px-4" to={process.env.PUBLIC_URL + "/contact"}>Contact</Link></div>
            </motion.ul>

          </motion.div>

          {/* <motion.div initial="initial" animate="enter" exit="exit" key="home" className="position-absolute" style = {{"top": 70 + "vh"}} id="topmenu">
            <motion.ul variants={postVariants} className="text-decoration-none list-unstyled pl-0">
              <li className="h4"><Link className="text-white text-decoration-none" to={process.env.PUBLIC_URL + "/about"}>About</Link></li>
              <li className="h4"><Link className="text-white text-decoration-none" to={process.env.PUBLIC_URL + "/works"}>Works</Link></li>
              <li className="nav-item"><Link className="nav-link" to={process.env.PUBLIC_URL + "/interests"}>Interests</Link></li>
              <li className="h4"><Link className="text-white text-decoration-none" to={process.env.PUBLIC_URL + "/contact"}>Contact</Link></li>
            </motion.ul>

          </motion.div> */}
          </AnimatePresence>
        </div>
      </div>
    )
  }
}
export default Home;
